import React, { Component } from 'react';
import Tile from './Tile';
import './App.css';
import firebase from './firebase.js';

const actions = ['eat','sleep','pray','eat','sleep','pray','eat','sleep','pray','eat','sleep','pray','eat','sleep','pray','eat','sleep','pray','eat','sleep','pray','eat','sleep','pray','eat','sleep','pray','eat','sleep','pray','eat','sleep','pray'];

class App extends Component {
  tiles = [];

  constructor() {
    super();

    var cols = 5;

    this.tiles = [];
    for (var i = 0; i < actions.length/cols; i++){
      var cur_action = actions[i*cols+j];

      // Even rows are all tiles
      if (i%2 == 0){
        for (var j=0; j < cols; j++){
          this.tiles.push(<Tile action={cur_action} type='action'/>);
        }
      }
      // Odd rows only have one tile
      // Every other odd row is on the left, the rest are on the right
      else {
        if (i%4 == 0){
          this.tiles.push(<Tile action={cur_action} type='action'/>);
          for (var j=0; j < cols-1; j++){
            this.tiles.push(<Tile type='empty'/>);
          }
        }
      }
    }

    this.state = {
      playerVal: "trying to connect to firebase.."
    }
  }

  componentDidMount() {
    const playersRef = firebase.database().ref('players');
    playersRef.on('value', (snapshot) => {
      let playerVal = snapshot.val();
      this.setState({
        players: playerVal
      });
    })
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1 className="App-logo">DrinkyLand</h1>
          <p>{this.state.players}</p>
        </header>
        <div class="board">
          {this.tiles}
        </div>
      </div>
    );
  }
}

export default App;
